    // page에 렌더링함
    res.render('updateDepartment', {
        title: "부서 테이블  갱신" ,dept_res
    });
});

// '수정'을 누르면 update query 실행
router.post('/employee', async (req,res) => {
    const vars=req.body;
    const data={
        Salary:vars.salary
    }
    // input이 저장된 data를 updateEmployee로 전달
